package com.openwaygroup.task.calculator;import org.apache.log4j.Logger;import java.nio.file.Path;import java.nio.file.Paths;/** * Class Main contains method main - entering point */public class Main {    static final Logger logger = Logger.getLogger(Calculator.class);    /**     * Starts calculation by calling method 'calculate'     *     * @param args: array of entering parameters, path to the source file and path to the file to write results     */    public static void main(String[] args) {        final Path input, output;        try {            if (args.length == 0) {                Path path = Paths.get(".");                input = Paths.get(path.toAbsolutePath() + "/src/main/resources/input.xml");                output = Paths.get(path.toAbsolutePath() + "/src/main/resources/output.xml");            } else {                input = Paths.get(args[0]);                output = Paths.get(args[1]);            }            Calculator calculator = new Calculator();            calculator.calculate(input, output);        } catch (IndexOutOfBoundsException e) {            logger.error("Not enough of the arguments");        }    }}