package com.openwaygroup.task.calculator;import org.w3c.dom.Document;import org.w3c.dom.Element;import javax.xml.parsers.DocumentBuilder;import javax.xml.parsers.DocumentBuilderFactory;import javax.xml.parsers.ParserConfigurationException;import javax.xml.transform.OutputKeys;import javax.xml.transform.Transformer;import javax.xml.transform.TransformerException;import javax.xml.transform.TransformerFactory;import javax.xml.transform.dom.DOMSource;import javax.xml.transform.stream.StreamResult;import java.io.File;import java.nio.file.Path;import java.util.LinkedList;public class ResultXMLWriter {    public static void writeResultToXML(Path path, LinkedList<String> resultList) throws ParserConfigurationException, TransformerException {        try {            DocumentBuilderFactory docFactory = DocumentBuilderFactory.newInstance();            DocumentBuilder docBuilder = docFactory.newDocumentBuilder();            Document document = docBuilder.newDocument();            document.setXmlStandalone(true);            Element rootElement = document.createElement("simpleCalculator");            document.appendChild(rootElement);            Element expressionResults = document.createElement("expressionResults");            rootElement.appendChild(expressionResults);            for (String result : resultList) {                Element expressionResult = document.createElement("expressionResult");                expressionResults.appendChild(expressionResult);                Element expResult = document.createElement("result");                expResult.appendChild(document.createTextNode(result));                expressionResult.appendChild(expResult);            }            TransformerFactory transformerFactory = TransformerFactory.newInstance();            Transformer transformer = transformerFactory.newTransformer();            DOMSource source = new DOMSource(document);            StreamResult result = new StreamResult(new File(path.toString()));            transformer.setOutputProperty(OutputKeys.INDENT, "yes");            transformer.setOutputProperty(OutputKeys.STANDALONE, "yes");            transformer.setOutputProperty("{http://xml.apache.org/xslt}indent-amount", "4");            transformer.transform(source, result);            System.out.println("File saved!");        } catch (ParserConfigurationException | TransformerException pce) {            pce.printStackTrace();        }    }}